// Generated by Google AI
// seed phrase "code me a webGL example of a rotating red triangle"
main();

function main() {
    const canvas = document.getElementById('glcanvas');
    const gl = canvas.getContext('webgl');

    if (!gl) {
        alert('Your browser does not support WebGL');
        return;
    }

    // Vertex shader program (GLSL)
    const vsSource = `
        attribute vec4 aVertexPosition;
        uniform vec2 uRotationVector;

        void main(void) {
            // Perform rotation manually using sin (x component) and cos (y component)
            vec2 rotatedPosition = vec2(
                aVertexPosition.x * uRotationVector.y + aVertexPosition.y * uRotationVector.x,
                aVertexPosition.y * uRotationVector.y - aVertexPosition.x * uRotationVector.x
            );
            gl_Position = vec4(rotatedPosition, 0.0, 1.0);
        }
    `;

    // Fragment shader program (GLSL)
    // This shader gives the triangle a solid red color
    const fsSource = `
        precision mediump float; // set float precision
        void main(void) {
            gl_FragColor = vec4(1.0, 0.0, 0.0, 1.0); // Red color
        }
    `;

    // Initialize a shader program
    const shaderProgram = initShaderProgram(gl, vsSource, fsSource);
    const programInfo = {
        program: shaderProgram,
        attribs: {
            position: gl.getAttribLocation(shaderProgram, 'aVertexPosition'),
        },
        uniforms: {
            rotationVector: gl.getUniformLocation(shaderProgram, 'uRotationVector'),
        },
    };

    const buffers = initBuffers(gl);

    let rotationAngle = 0.0;
    let lastTime = 0;

    // Draw the scene repeatedly
    function render(now) {
        now *= 0.001; // convert to seconds
        const deltaTime = now - lastTime;
        lastTime = now;

        drawScene(gl, programInfo, buffers, rotationAngle);
        rotationAngle += deltaTime * 3; // Adjust rotation speed

        requestAnimationFrame(render);
    }

    requestAnimationFrame(render);
}

// --- WebGL Initialization and Drawing Helper Functions ---

function initBuffers(gl) {
    const vertices = [
         0.0,  0.7, // Top
        -0.5, -0.5, // Bottom left
         0.5, -0.5  // Bottom right
    ];

    const vertexBuffer = gl.createBuffer();
    gl.bindBuffer(gl.ARRAY_BUFFER, vertexBuffer);
    gl.bufferData(gl.ARRAY_BUFFER, new Float32Array(vertices), gl.STATIC_DRAW);

    return { position: vertexBuffer };
}

function drawScene(gl, programInfo, buffers, rotationAngle) {
    gl.clearColor(0.0, 0.0, 0.0, 1.0); // Clear to black, fully opaque
    gl.clearDepth(1.0); // Clear everything
    gl.enable(gl.DEPTH_TEST); // Enable depth testing
    gl.depthFunc(gl.LEAST); // Near things obscure far things

    gl.clear(gl.COLOR_BUFFER_BIT | gl.DEPTH_BUFFER_BIT);

    gl.useProgram(programInfo.program);

    // Calculate the sine and cosine of the current rotation angle
    const sinAngle = Math.sin(rotationAngle);
    const cosAngle = Math.cos(rotationAngle);
    // Pass the rotation vector to the shader program's uniform
    gl.uniform2f(programInfo.uniforms.rotationVector, sinAngle, cosAngle);

    // Bind the position buffer
    gl.bindBuffer(gl.ARRAY_BUFFER, buffers.position);
    gl.vertexAttribPointer(
        programInfo.attribs.position,
        2,          // size (2 components per iteration: x, y)
        gl.FLOAT,   // type of data
        false,      // normalize
        0,          // stride
        0);         // offset
    gl.enableVertexAttribArray(programInfo.attribs.position);

    // Draw the triangle
    gl.drawArrays(gl.TRIANGLES, 0, 3);
}

function initShaderProgram(gl, vsSource, fsSource) {
    const vertexShader = loadShader(gl, gl.VERTEX_SHADER, vsSource);
    const fragmentShader = loadShader(gl, gl.FRAGMENT_SHADER, fsSource);

    const shaderProgram = gl.createProgram();
    gl.attachShader(shaderProgram, vertexShader);
    gl.attachShader(shaderProgram, fragmentShader);
    gl.linkProgram(shaderProgram);

    if (!gl.getProgramParameter(shaderProgram, gl.LINK_STATUS)) {
        alert('Unable to initialize the shader program: ' + gl.getProgramInfoLog(shaderProgram));
        return null;
    }

    return shaderProgram;
}

function loadShader(gl, type, source) {
    const shader = gl.createShader(type);
    gl.shaderSource(shader, source);
    gl.compileShader(shader);

    if (!gl.getShaderParameter(shader, gl.COMPILE_STATUS)) {
        alert('An error occurred compiling the shaders: ' + gl.getShaderInfoLog(shader));
        gl.deleteShader(shader);
        return null;
    }

    return shader;
}
